/**
 * Search diagnoses by code or name
 * Matches partial strings in both code and name
 */
export function searchDiagnoses(query: string): DiagnosisItem[] {
  if (!query || query.trim() === '') {
    return [];
  }

  const lowerQuery = query.toLowerCase().trim();

  return icd10Diagnoses.filter((diagnosis) => {
    const codeMatch = diagnosis.code.toLowerCase().includes(lowerQuery);
    const nameMatch = diagnosis.name.toLowerCase().includes(lowerQuery);
    const parentMatch = diagnosis.parentCode?.toLowerCase().includes(lowerQuery);

    return codeMatch || nameMatch || parentMatch;
  });
}

/**
 * Get diagnoses by section
 */
export function getDiagnosesBySection(section: string): DiagnosisItem[] {
  return icd10Diagnoses.filter((d) => d.section === section);
}

/**
 * Get diagnosis by code
 */
export function getDiagnosisByCode(code: string): DiagnosisItem | undefined {
  return icd10Diagnoses.find((d) => d.code === code);
}

/**
 * Get child diagnoses for a parent code
 */
export function getChildDiagnoses(parentCode: string): DiagnosisItem[] {
  return icd10Diagnoses.filter((d) => d.parentCode === parentCode);
}

/**
 * Get parent diagnosis for a given code
 */
export function getParentDiagnosis(code: string): DiagnosisItem | undefined {
  const diagnosis = getDiagnosisByCode(code);
  if (!diagnosis?.parentCode) {
    return undefined;
  }
  return getDiagnosisByCode(diagnosis.parentCode);
}

/**
 * Check if a diagnosis requires a child to be selected
 * These are level 2 items with level 3 children (e.g., F40.0, F43.2, F45.3, F50.0, F60.3)
 */
export function requiresChildSelection(code: string): boolean {
  const diagnosis = getDiagnosisByCode(code);
  if (!diagnosis || diagnosis.level !== 2 || !diagnosis.hasChildren) {
    return false;
  }
  // Check if it has level 3 children
  const children = getChildDiagnoses(code);
  return children.length > 0 && children.some((child) => child.level === 3);
}
